import anchorsFunction from "./functions/anchorsFunction.js";
import showMenu from "./functions/burgerMenu.js";
import ActionCard from "./classes/ActionClass.js";
import ActionsArr from "./data/actionsData.js";

const anchors = document.querySelectorAll('.js-anchor');
anchorsFunction(anchors);

const burgerMenu = document.querySelector('.js-menu');
const firstLine = document.querySelector('.js-first-line');
const secondLine = document.querySelector('.js-second-line');
const menuWrapper = document.querySelector('.js-menu-items');

showMenu(burgerMenu, firstLine, secondLine, menuWrapper);


const selector = document.querySelector('#actions');

ActionsArr.forEach( ({ date, src, text }) => {
    console.log(src);
    new ActionCard(src, date, text).render(selector);
  });


document.querySelector('.actions__load-more-btn').onclick = function() {
    window.location.href = 'indexCardsPost.html';
};



